CREATE DATABASE IF NOT EXISTS test;
use test;

CREATE TABLE sightseeings (
    id INT(255) auto_increment,
    prefecture_id INT(255),
    name VARCHAR(255),
    image VARCHAR(255),
    description TEXT,
    city VARCHAR(255),
    updated_at datetime,
	created_at datetime,
	deleted_at datetime,
    PRIMARY KEY(id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

